function startstream(){console.log("infunction");var e=document.getElementById("streamselect").value;var t=getCookie("radioID");$.ajax({url:"/cgi-bin/api.php?q=genconfig&name="+e+"&id="+t,dataType:"json",success:function(e){json=e;console.log(json);if(json.result=="success"){bootstrap_alert.success("Success: Stream started");window.setTimeout(closealert,5e3)}else{bootstrap_alert.warning("Error: Er is een fout opgetreden (Error code: 402)");window.setTimeout(closealert,5e3)}}})}function deletecookie(){document.cookie="username=; expires=Thu, 01 Jan 1970 00:00:00 GMT"}function closealert(){$("#alertmssg").alert("close")}function saveStream(){var e=document.getElementById("newStreamName").value;var t=document.getElementById("newStreamURL").value;var n=getCookie("radioID");$.ajax({url:"/cgi-bin/api.php?q=addstream&name="+e+"&url="+t+"&id="+n,dataType:"json",success:function(e){json=e;$("#myModal").modal("hide");getJsonFile();populateSavedStreams(document.getElementById("streamselect"),json.streams)}})}function saveAlarm(){var e=document.getElementById("streamselect2").value;var t=document.getElementById("newAlarmType").value;var n=document.getElementById("timeAlarm").value+document.getElementById("dateAlarm");var r=document.getElementById("newAlarmDesc").value;var i=getCookie("radioID");$.ajax({url:"/cgi-bin/api.php?q=addalarm&id="+i+"&time="+n+"&type="+t+"&stream="+e+"&desc="+r,dataType:"json",success:function(e){json=e;console.log(json);$("#myModal").modal("hide");getJsonFile();populateSavedAlarms(document.getElementById("alarmselect"),json.alarms)}})}function getCookie(e){var t=e+"=";var n=document.cookie.split(";");for(var r=0;r<n.length;r++){var i=n[r].trim();if(i.indexOf(t)==0)return i.substring(t.length,i.length)}return""}function checkCookie(){var e=getCookie("radioName");if(e!=""){return}else{window.location="login.html"}}function load(){checkCookie();getJsonFile()}function getJsonFile(){var e;$.ajax({url:"/cgi-bin/api.php",dataType:"json",success:function(t){e=t;populateSavedStreams(document.getElementById("streamselect"),e.streams);populateSavedStreams(document.getElementById("streamselect2"),e.streams);populateSavedAlarms(document.getElementById("alarmselect"),e.alarms);populateHeader(document.getElementById("header"))}})}bootstrap_alert=function(){};bootstrap_alert.warning=function(e){$("#alert_placeholder").html('<div class="alert alert-danger alert-dismissable"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button><span>'+e+"</span></div>")};bootstrap_alert.success=function(e){$("#alert_placeholder").html('</br><div class="alert alert-success alert-dismissable" id="alertmssg"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button><span>'+e+"</span></div>")}